import UIKit

protocol RecordsStore {
    func fetchRecords(completionHandler: @escaping () -> Void)
}

class RecordsWorker {
    var recordsStore: RecordsStore
    
    init(recordsStore: RecordsStore) {
        self.recordsStore = recordsStore
    }
    
    func fetchRecords(completionHandler: @escaping () -> Void) {
        recordsStore.fetchRecords {
            completionHandler()
        }
    }
}
