@testable import SHA
import XCTest

class MainInteractorTests: XCTestCase {
    // MARK: - Subject under test

    var sut: MainInteractor!

    // MARK: - Test lifecycle

    override func setUp() {
        super.setUp()
        setupMainInteractor()
    }

    override func tearDown() {
        super.tearDown()
    }

    // MARK: - Test setup

    func setupMainInteractor() {
        sut = MainInteractor()
    }

    // MARK: - Test doubles

    class MainPresentationLogicSpy: MainPresentationLogic {
        var presentSomethingCalled = false

        func presentSomething(response: Main.CurrentDate.Response) {
            presentSomethingCalled = true
        }
    }

    // MARK: - Tests

    func testDoSomething() {
        // Given
        let spy = MainPresentationLogicSpy()
        sut.presenter = spy
        let request = Main.CurrentDate.Request()

        // When
        sut.fetchCurrentDate(request: request)

        // Then
        XCTAssertTrue(spy.presentSomethingCalled, "doSomething(request:) should ask the presenter to format the result")
    }
}
